var menuAimDelay = function() {
	var $menu = $('.js-menu-aim-delay');

	if ($(window).width() > 991) {
		$menu.menuAim({
			activate: activateSubmenu,
			deactivate: deactivateSubmenu,
			// exitMenu: function() { return true; }
			exitMenu: deactivateAll,
		});
	}
	// jQuery-menu-aim: the following JS is used to show and hide the submenu
	// contents. Again, this can be done in any number of ways. jQuery-menu-aim
	// doesn't care how you do this, it just fires the activate and deactivate
	// events at the right times so you know when to show and hide your submenus.
	function activateSubmenu(row) {
		// console.log('activateMenu');
		var $row = $(row);


		// Keep the currently activated row's highlighted look
		$row.addClass("is-active");
		// console.log($row, "row activate");


		// var dataAttr = $row.attr('data-hover');
		// console.log(dataAttr , "row data atr");
		// $row.closest('.dropdown-menu__list-wrap').find(".section-background__item[data-show='" + dataAttr + "']").addClass('item-active');
		// $row.closest('.dropdown-menu__list-wrap').find(".section-background__item[data-show='" + dataAttr + "']").trigger('mouseenter');
	}

	function deactivateSubmenu(row) {
		// console.log('deactivateSubmenu');
		var $row = $(row);

		// Hide the submenu and remove the row's highlighted look
		$row.removeClass("is-active");

		// console.log($row, "row deactivate"):
		// $('.section-background .section-background__item').removeClass('item-active');
	}

	function deactivateAll(row) {
		var $rows = $(row).find('.main-menu__item.is-active');
		// $menu.clearActiveRow();
		// console.log($rows, "deactivateAll");
		// Hide the submenu and remove the row's highlighted look
		$rows.removeClass("is-active");


		// $(".main-menu__item").removeClass("is-active");
		// $('.section-background .section-background__item').removeClass('item-active');
		return true;
	}

};


