var magicLine = function() {
	var $el, leftPos, newWidth;

	if ($(".js-magic-line").length === 0) {
		$(".js-section-nav").append("<li class='magic-line js-magic-line'></li>");
	}

	var $magicLine = $(".js-magic-line");
	var posLeft = $(".current").length > 0 ? $(".current").position().left : 0;
	var initialColor = $(".current").length > 0 ? $(".current").attr('data-color') : $(".js-magic-line").css('background-color');

	$magicLine
	.width($(".js-section-nav .current").width())
	.css("left", posLeft)
	.data("origLeft", $magicLine.position().left)
	.data("origWidth", $magicLine.width())
	.css("background-color", initialColor);

	$(".js-section-nav").find(".section-nav__list-item").hover(function() {
		$el = $(this);
		leftPos = $el.position().left;
		newWidth = $el.width();
		newColor = $el.attr('data-color');

		$magicLine.stop().animate({
			left: leftPos,
			width: newWidth,
			backgroundColor: newColor
		});
	},
	function() {
		$magicLine.stop().animate({
			left: $magicLine.data("origLeft"),
			width: $magicLine.data("origWidth"),
			backgroundColor: initialColor
		});
	});
};

/* resize elemenata na window height na mobitelima */
var resizeEL = function() {
	var vph = window.innerHeight;

	var mq = window.matchMedia( "(min-width: 992px)" );
	if (mq.matches) {
		if ( $('.home-slider .swiper-container').length > 0 ) {
			$('.home-slider .swiper-container').css({'height': ''});
		}
		if ( $('.site-header .main-menu').length > 0 ) {
			$('.site-header .main-menu').css({'height': ''});
		}
	}
	else {
		if ( $('.home-slider .swiper-container').length > 0 ) {
			$('.home-slider .swiper-container').css({'height': (vph - 66) + 'px'});
		}
		if ( $('.site-header .main-menu').length > 0 ) {
			$('.site-header .main-menu').css({'height': (vph) + 'px'});
		}
	}
};

