// magazin recipes: multi select filters
var multiSelect  = function() {

	$(".js-filter-dropdown").each(function(index, item){

		$(this).on('click', function(e) {
			e.stopPropagation();
		});

		// js-food-type
		var $el = $(item);

		/*$el.find(".main-label").on('click', function() {
			$el.find("dd ul").slideToggle('fast');
			$(this).toggleClass('open');
		});*/
 
		var arr = [];
		var checked_items = $el.find('.dropdown__list-item input[type="checkbox"]');
		var viewBtn = $el.find('.xFilter');

		checked_items.each( function(index){
			var value = $(this).siblings(".checkbox-label").html();
			// console.log(value, "value first");

			if ($(this).is(":checked")) {
				arr.push('<span>' + value + '</span>');
			}
		});

		if (arr.length === 0) {
			$el.find(".selected-filter").hide();
		} else {
			$el.find(".selected-filter").show();
		}


		var html = arr.join(', ');
		$el.find('.selected-filter__item-list').html(html);

		function changeHandle(e)
		{
			var arr = [];
			$el.find('.dropdown__list-item input[type="checkbox"]').each( function(index)
			{
				if ($(this).is(":checked"))
				{
					var value = $(this).siblings(".checkbox-label").html();
					arr.push('<span>' + value + '</span>');
				}
			});

			checkedEls = $el.find('input[type="checkbox"]:checked');

			var html = arr.join(', ');
			$el.find('.selected-filter__item-list').html(html);

			if (arr.length === 0) {
				$el.find(".selected-filter").hide();
			} else {
				$el.find(".selected-filter").show();
			};

			//console.log( e.target, viewBtn[0] );
			//console.log( e.target.isSameNode(viewBtn[0]) );
			if( e.target.isSameNode(viewBtn[0]) || !isMobile.any() )
			{
				var e = new CustomEvent('multiSelectChange',{
					bubbles:true,
					detail:{
						checked:checkedEls,
					},
				});
				this.dispatchEvent(e);
			}
		};

		/*if( isMobile.any() )
		{
			viewBtn.on('click', changeHandle);
		}*/
		checked_items.on("change", changeHandle);

	});

	//mobile close drop on btn click
	$(".filter__wrapper .xFilter").on('click', function(e){
		e.preventDefault();

		$('.dropdown__list').slideUp(500);
		$('.js-dropdown-toggle').removeClass("is-active");
	});

	//filter checkboxes
	$(".js-clear-filters").on('click', function(e){
		e.preventDefault();
		input = $(this).parents('.dropdown__list').find('input[type="checkbox"]');
		//input.checked
		input.prop('checked', false);
		ids = [];
		input.each(function(idx){
			ids.push(parseInt( $(this).data('filterid') ));
		});
		var e = new CustomEvent('multiSelectReset',{
			bubbles:true,
			detail:{ids:ids},
		});
		$(this).parents('.selected-filter').hide();
		this.dispatchEvent(e);
	});

};


