var animationText = {
	
	init: function() {
		var self = this;

		if ( $('.animation').length === 0 ) {
			// console.log('no animations');
			return;
		}

		this.create();
	},
	create: function() {

		$('.animation').each(function(item, index){
			var self = this;
			var children = $(self).children();

			console.log(self, children);

			children.each(function(item, index){
				var self = this;

				var theString = this;
				var theStringText = this.textContent;
				console.log(this.textContent);
				var wordArray = [];
				var wordArray = theStringText.split(' ');

				theString.innerHTML= "";
				wordArray.forEach(function(item, index) {

					let string= "";

					for (var i = 0; i < item.length; i++) {
						
						if (i == 0 && item.length == 1 && item != " ") {
							let letter = ('<span class="animation-item"><span class="animation-letter">' + item.charAt(i) + '</span></span> ');
							string += letter;
						} else if(i == 0 && item != " " && item.length >= 1) {
							let letter = ('<span class="animation-item"><span class="animation-letter">' + item.charAt(i) + '</span>');
							string += letter;
						} else if(i == 0 && i == (item.length - 1)) {
							let letter = ('<span class="animation-letter">' + item.charAt(i) + '</span></span> ');
							string += letter;
						} else if (i == (item.length - 1)) {
							let letter = ('<span class="animation-letter">' + item.charAt(i) + '</span></span> ');
							string += letter;
						}else {
							let letter = ('<span class="animation-letter">' + item.charAt(i) + '</span>');
							string += letter;
						}
					}

					theString.innerHTML += string;
				});
			});	
		})	
	},
}

var animationTimeout = function(delay, item){
	// console.log(item);

	setTimeout(function(){
		item.addClass("animate");
	}, delay);
}

var animationDelay = function() {

	var self = this;

	if($('.animation').hasClass("reveal") ){

		$('.swiper-slide-active .animation').each(function(item, index){
			var self = this;
			var delay = 0;
			
			// $(this).removeClass("anim-hide");

			let animationWrap = $(self).children();
			let animationItem = animationWrap.children();

			for (var i = 0; i < animationItem.length; i++) {
				let item = animationItem.eq(i);
				let allChildrenItem = item.children();

				if(i < 1) {
					animationTimeout(0.1, item);
					delay += allChildrenItem.length * 80;
					
				} else  {
					animationTimeout(delay, item);
					delay += allChildrenItem.length * 80;
				}
			}
			delay = 0;
		});
	}	
};

