xSendInquiry = {
	selector:'.xForm',
	forms:[],
	init: function()
	{
		$(this.selector).each(function (i, el)
		{
			$el = $(el);
			$button = $el.find('input[type="submit"]');
			$res = $el.find('div.xFormResponse')
			form = {
				index: i,
				form: $el,
				button: $button,
				res: $res,
			};
			this.forms.push(form);
			$button.on('click touchstart', form, function(e)
			{
				e.preventDefault();
				url = e.data.form.attr('action');
				if ( url !== undefined && url !== '')
				{
					form.form.find('input').removeClass('error');
					form.form.find('textarea').removeClass('error');
					form.form.find('label').removeClass('error');
			
					form.res.find('.error').hide();
					form.res.find('.success').hide();

					var formData = new FormData(e.data.form.get(0));
					var formObj = {formIdx : e.data.index};
					formData.forEach(function(v, k){
						formObj[k] = v;
					});
					// console.log(formObj)
					fwajax.langSegment = lang_segment;
					fwajax.phpExecute({ url:url }, formObj);
				}
				// return false;
			});
		}.bind(this));
	},
	defaultSuccess: function(data)
	{
		
		data = JSON.parse(data);
		console.log(this, data);
		var form = this.forms[parseInt(data.formIdx)];


		if(data.success)
		{
			form.res.find('.success').show();
			form.form.reset();
		}
		else
		{
			for(err in data.form_error)
			{
				// console.log(err);
				if(data.form_error[err])
				{
					if(err=="message")
					{
						form.form.find("textarea[name='"+err+"']").addClass('error');
						form.res.find('.errorGeneric').show();
					}
					else if(err=="privacy_policy")
					{
						form.form.find("label[for='"+err+"']").addClass('error');
						form.res.find('.errorPrivacy').show();
					}
					else
					{
						form.form.find("input[name='"+err+"']").addClass('error');
						form.res.find('.errorGeneric').show();
					}
				}
			}
			form.res.find('.success').hide();
		}

	}
	
};

