xFilterLogistics = {
	searchSelector: '.xFilterLogistics',
	searchTextSelector: 'input.search-head__search-input',
	loadMoreSelector: '.xLogisticsLoadMore',
	searchTerm:'',
	page:1,
	section_id:0,
	init: function()
	{
		if(typeof(currentPage)!=='undefined')
		{
			this.page = parseInt(currentPage);
		}
		if(typeof(section_id)!=='undefined')
		{
			this.section_id = parseInt(section_id);
		}
		if(typeof(searchTerm)!=='undefined')
		{
			this.searchTerm = searchTerm;
		}
		$(this.searchSelector).each(function (i, el)
		{
			$(el).on('click touchstart', function(e)
			{
				e.preventDefault()
				this.searchTerm = $(this.searchTextSelector).val();				
				this.filterLogistics();
			}.bind(this));

		}.bind(this));

		$(this.loadMoreSelector).each(function (i, el)
		{
			$(el).on('click touchstart', function(e)
			{
				e.preventDefault()
				this.paginate();
			}.bind(this));
		}.bind(this));

	},
	buildParams: function(noId=false)
	{
		params = {};
		if(this.page > 1)
		{
			params.pg = this.page;
		}
		if(this.section_id && !noId)
		{
			params.id = this.section_id;
		}
		if(this.searchTerm !== '')
		{
			params.s = this.searchTerm;
		}

		return params;
	},
	filterLogistics: function()
	{
		var url='/xajax/filterLogisticsLoadMore';
		fwajax.langSegment = lang_segment;
		this.page=1;
		var params = this.buildParams();
		fwajax.phpExecute({ url:url }, params);
	},
	updateState: function()
	{
		var queryString = '?' + $.param( this.buildParams(true) );
		var urlPath = window.location.pathname + ( queryString!=='?'?queryString:'' );
		var title = document.pageTitle + " Page " + this.page;
		window.history.pushState({"pageTitle":title, urlPath: urlPath},title, urlPath);
	},
	filterSuccess: function(data)
	{
		data = JSON.parse(data);
		if(data.success)
		{
			$('div.products-list__content > .products-list__item').remove();
			$('div.products-list__content').append(data.html);
			if(!data.has_more)
			{
				$(this.loadMoreSelector).parents('.row').eq(0).hide();
			}else
			{
				$(this.loadMoreSelector).parents('.row').eq(0).show();
			}
			this.updateState();
			//animacija
			revealElement();
		}
		else
		{
			console.log('ERROR:',data);
		}
	},
	paginate: function()
	{
		var url='/xajax/filterLogisticsLoadMore';
		fwajax.langSegment = lang_segment;
		this.page=this.page+1;
		var params = this.buildParams();
		fwajax.phpExecute({ url:url }, params);
	},
	paginateSuccess: function(data)
	{
		data = JSON.parse(data);
		if(data.success)
		{
			// console.log('succ:',data);
			$('div.products-list__content').append(data.html);
			if(!data.has_more)
			{
				$(this.loadMoreSelector).parents('.row').eq(0).hide();
			}else
			{
				$(this.loadMoreSelector).parents('.row').eq(0).show();
			}
			this.updateState();

			//animacija
			revealElement();
		}
		else
		{
			console.log('ERROR:',data);
		}
	}
};

