var Newsletter =
{
	init: function()
	{

		$('.xnewsletter_submit').on('click touchstart', function(e)
		{
			e.preventDefault();
			this.subscribe();
		}.bind(this));

		$('.newsletter__terms-label').on('click touchstart', function(e)
		{
			e.preventDefault();
			$el = $('input[name='+$(this).prop('for')+']');
			$el.prop('checked', !$el.prop('checked'));
		});
	},
	subscribe: function()
	{
		var email=$('.newsletter input[name=email]').val();
		var privola=$('.newsletter input[name=privola]').prop('checked')?$('.newsletter input[name=privola]').val():'ne';
		var privola2=$('.newsletter input[name=privola2]').prop('checked')?$('.newsletter input[name=privola2]').val():'ne';
		if (email == 'email@email.com')
		{
			email = '';
		}
		fwajax.langSegment = lang_segment;
		fwajax.phpExecute(
			{url: '/xajax/newsletterSubscribe'},
			email, privola, privola2
		);
	},
	callback_subscribe: function(data)
	{
		data = JSON.parse(data);
		// console.log(data);
		if (data.success)
		{
			$('.c-message__newsletter').hide();
			$('.c-message__newsletter--success').show();
		}
		else
		{
			$('.c-message__newsletter').hide();
			if(data.form_error.email_taken)
				$('.errorEmailTaken').show();
			else if(data.form_error.privola || data.form_error.privola2)
				$('.errorCheckbox').show();
			else
				$('.errorOther').show();
		}
	},
}; 



